#pragma acc routine
_class_Sqq_w_monitor *class_Sqq_w_monitor_trace(_class_Sqq_w_monitor *_comp
  , _class_particle *_particle) {
  ABSORBED=SCATTERED=RESTORE=0;
  #define radius (_comp->_parameters.radius)
  #define yheight (_comp->_parameters.yheight)
  #define qax (_comp->_parameters.qax)
  #define qaz (_comp->_parameters.qaz)
  #define qbx (_comp->_parameters.qbx)
  #define qbz (_comp->_parameters.qbz)
  #define qamin (_comp->_parameters.qamin)
  #define qamax (_comp->_parameters.qamax)
  #define qbmin (_comp->_parameters.qbmin)
  #define qbmax (_comp->_parameters.qbmax)
  #define Emin (_comp->_parameters.Emin)
  #define Emax (_comp->_parameters.Emax)
  #define nqa (_comp->_parameters.nqa)
  #define nqb (_comp->_parameters.nqb)
  #define nE (_comp->_parameters.nE)
  #define filename (_comp->_parameters.filename)
  #define nowritefile (_comp->_parameters.nowritefile)
  #define nosum (_comp->_parameters.nosum)
  #define vix (_comp->_parameters.vix)
  #define viy (_comp->_parameters.viy)
  #define viz (_comp->_parameters.viz)
  #define M_N (_comp->_parameters.M_N)
  #define M_p (_comp->_parameters.M_p)
  #define M_p2 (_comp->_parameters.M_p2)
  #define M_Ns (_comp->_parameters.M_Ns)
  #define M_ps (_comp->_parameters.M_ps)
  #define M_p2s (_comp->_parameters.M_p2s)
  #define dE (_comp->_parameters.dE)
  SIG_MESSAGE("[_Sqqw_trace] component Sqqw=Sqq_w_monitor() TRACE [/usr/share/mcstas/3.3/tools/Python/mcrun/../mccodelib/../../../monitors/Sqq_w_monitor.comp:102]");

     int i,j,k;
    //double rx,ry,rz,rvx,rvy,rvz,rt,rsx,rsy,rsz,rp;
    double rvx,rvy,rvz;
    double Ei,Ef,E,Ki,Kf;
    double qx,qy,qz;
    double qqa, qqb;
    double kix,kiy,kiz;
    double kfx,kfy,kfz;
    double t0,t1;
    

    double nx,ny,nz;
    nx=vx;
    ny=vy;
    nz=vz;
    NORM(nx,ny,nz);
    double v_div=scalar_prod(nx, ny, nz, 0, 1, 0);
    /* Initial check to see if this neutron should be counted or not... */ 
    if (fabs(v_div)<=yheight/radius && cylinder_intersect(&t0, &t1, x, y, z, vx, vy, vz, radius, yheight)) {
	if(t0<0 && t1>0) {
	/* This one hits our cylindrical band, arriving from inside */
	
	int fail;
	rvx = particle_getvar(_particle,vix,&fail); if(fail) rvx=0;
	rvy = particle_getvar(_particle,viy,&fail); if(fail) rvy=0;
	rvz = particle_getvar(_particle,viz,&fail); if(fail) rvz=0;

	/* Calculate energy transfer */
  	Ei = VS2E*(rvx*rvx + rvy*rvy + rvz*rvz);
	Ef = VS2E*(  vx*vx +  vy*vy +    vz*vz);
	E=Ei-Ef;
	
	/* calculate k vectors and momentum transfer*/
  	kix=rvx;
	kiy=rvy;
	kiz=rvz;
	kfx=vx;
	kfy=vy;
	kfz=vz;
	NORM(kix, kiy, kiz);
	NORM(kfx, kfy, kfz);
	
	/* K-vector lengths */
 	Ki=V2K*sqrt((rvx*rvx)+(rvy*rvy)+(rvz*rvz));
	Kf=V2K*sqrt((vx*vx)+(vy*vy)+(vz*vz));
	kix=Ki*kix; kiy=Ki*kiy; kiz=Ki*kiz; 
	kfx=Kf*kfx; kfy=Kf*kfy; kfz=Kf*kfz; 
	
	qx=kix-kfx;
	qy=kiy-kfy;
	qz=kiz-kfz;
  	/* Calculate projections along qa and qb */
	qqa = qx*qax + qz*qaz;
	qqb = qx*qbx + qz*qbz;
	
	/* Check if we are within the selected q/e range */
	if (qqa <= qamax && qqa >=qamin && qqb <=qbmax && qqb>=qbmin && E<= Emax && E>=Emin) {
	  
	  i = floor((qqa - qamin)*nqa/(qamax - qamin));
	  j = floor((qqb - qbmin)*nqb/(qbmax - qbmin));
	  k = floor((E - Emin)*nE/(Emax - Emin));
	  
	  double p2 = p*p;
  	  #pragma acc atomic
	  M_N[k][i][j] = M_N[k][i][j] + 1;
          #pragma acc atomic
          M_p[k][i][j] = M_p[k][i][j] + p;
          #pragma acc atomic
	  M_p2[k][i][j] = M_p2[k][i][j] + p2;
	  
	  #pragma acc atomic
	  M_Ns[i][j] = M_Ns[i][j] + 1 ;
          #pragma acc atomic
	  M_ps[i][j] = M_ps[i][j] + p;
	  #pragma acc atomic
	  M_p2s[i][j] = M_p2s[i][j] + p2;
	  
	  SCATTER;
	  }
	} 
      } 
    /* Stuff that don't hit cylinder properly is disregarded */
#ifndef NOABSORB_INF_NAN
  /* Check for nan or inf particle parms */ 
  if(isnan(p)  ||  isinf(p)) ABSORB;
  if(isnan(t)  ||  isinf(t)) ABSORB;
  if(isnan(vx) || isinf(vx)) ABSORB;
  if(isnan(vy) || isinf(vy)) ABSORB;
  if(isnan(vz) || isinf(vz)) ABSORB;
  if(isnan(x)  ||  isinf(x)) ABSORB;
  if(isnan(y)  ||  isinf(y)) ABSORB;
  if(isnan(z)  ||  isinf(z)) ABSORB;
#else
  if(isnan(p)  ||  isinf(p)) printf("NAN or INF found in p,  %s (particle %lld)\n",_comp->_name,_particle->_uid);
  if(isnan(t)  ||  isinf(t)) printf("NAN or INF found in t,  %s (particle %lld)\n",_comp->_name,_particle->_uid);
  if(isnan(vx) || isinf(vx)) printf("NAN or INF found in vx, %s (particle %lld)\n",_comp->_name,_particle->_uid);
  if(isnan(vy) || isinf(vy)) printf("NAN or INF found in vy, %s (particle %lld)\n",_comp->_name,_particle->_uid);
  if(isnan(vz) || isinf(vz)) printf("NAN or INF found in vz, %s (particle %lld)\n",_comp->_name,_particle->_uid);
  if(isnan(x)  ||  isinf(x)) printf("NAN or INF found in x,  %s (particle %lld)\n",_comp->_name,_particle->_uid);
  if(isnan(y)  ||  isinf(y)) printf("NAN or INF found in y,  %s (particle %lld)\n",_comp->_name,_particle->_uid);
  if(isnan(z)  ||  isinf(z)) printf("NAN or INF found in z,  %s (particle %lld)\n",_comp->_name,_particle->_uid);
#endif
  #undef radius
  #undef yheight
  #undef qax
  #undef qaz
  #undef qbx
  #undef qbz
  #undef qamin
  #undef qamax
  #undef qbmin
  #undef qbmax
  #undef Emin
  #undef Emax
  #undef nqa
  #undef nqb
  #undef nE
  #undef filename
  #undef nowritefile
  #undef nosum
  #undef vix
  #undef viy
  #undef viz
  #undef M_N
  #undef M_p
  #undef M_p2
  #undef M_Ns
  #undef M_ps
  #undef M_p2s
  #undef dE
  return(_comp);
} /* class_Sqq_w_monitor_trace */

